],"payable":false,"stateMutability":"nonpayable","type":"function"},{"constant":false,"inputs":[{"internalType":"address","name":"_owner","type":"address"}],"name":"setProxyOwner","outputs":[],"payable":false,"stateMutability":"nonpayable","type":"function"},{"constant":false,"inputs":[{"internalType":"address","name":"newOwner","type":"address"}],"name":"transferOwnership","outputs":[],"payable":false,"stateMutability":"nonpayable","type":"function"}],"devdoc":{"details":"StakingRewardsOs contract should be upgradable. Used UpgradableProxy. StakingRewardsOsStorage is deployed with the upgradable functionality by using this contract instead, that inherits from UpgradableProxy with the possibility of being enhanced and re-deployed.","methods":{"getCheckpointBlockNumber(uint256)":{"params":{"_checkpointTimestamp":"Checkpoint timestamp"}},"getImplementation()":{"return":"Address of the implementation."},"getProxyOwner()":{"return":"Address of the owner."},"isOwner()":{"details":"Returns true if the caller is the current owner."},"owner()":{"details":"Returns the address of the current owner."},"setImplementation(address)":{"details":"Wrapper for _setImplementation that exposes the function as public for owner to be able to set a new version of the contract as current pointing implementation.","params":{"_implementation":"Address of the implementation."}},"setProxyOwner(address)":{"params":{"_owner":"Address of the owner."}},"transferOwnership(address)":{"details":"Transfers ownership of the contract to a new account (`newOwner`). Can only be called by the current owner."}},"title":"StakingRewardsOs Proxy contract."},"userdoc":{"methods":{"getImplementation()":{"notice":"Return address of the implementation."},"getProxyOwner()":{"notice":"Return address of the owner."},"setImplementation(address)":{"notice":"Set address of the implementation."},"setProxyOwner(address)":{"notice":"Set address of the owner."}}}},"settings":{"compilationTarget":{"contracts/governance/Staki